	function getWeatherWidget($cidade, $apiKey)
	{

		static $icons = [
			'01d' => '',
			'01n' => '',
			'02d' => '',
			'02n' => '',
			'03d' => '',
			'03n' => '',
			'04d' => '',
			'04n' => '',
			'09d' => '',
			'09n' => '',
			'10d' => '',
			'10n' => '',
			'11d' => '',
			'11n' => '',
			'13d' => '',
			'13n' => '',
			'50d' => '',
			'50n' => ''
		];

		// Verifica e cria o diretório de cache, se necessário
		if (!is_dir('./cache')) {
			mkdir('./cache/');
		}

		$cacheFile = "./cache/" . md5($cidade) . ".json";
		$cacheLifetime = 600; // 10 minutos para a atualização do cache
		$extendedCacheLifetime = 3600; // 1 hora para uso do cache estendido
		$useExtendedCache = false;

		// Verifica se o cache existe e decide se deve usar o cache estendido
		if (file_exists($cacheFile)) {
			$cacheAge = time() - filemtime($cacheFile);
			if ($cacheAge < $cacheLifetime) {
				$dados = json_decode(file_get_contents($cacheFile), true);
			} elseif ($cacheAge < $extendedCacheLifetime) {
				$useExtendedCache = true;
			}
		}
		// Solicitação à API ou uso do cache
		if (!isset($dados) || $useExtendedCache) {
			$url = "https://api.openweathermap.org/data/2.5/weather?q=" . urlencode($cidade) . "&units=metric&lang=pt_br&appid=" . $apiKey;
			$ch = curl_init();
			curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
			curl_setopt($ch, CURLOPT_URL, $url);
			$resposta = curl_exec($ch);
			$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
			curl_close($ch);

			if ($httpCode == 200) {
				$dados = json_decode($resposta, true);
				file_put_contents($cacheFile, $resposta);
			} elseif ($useExtendedCache) {
				$dados = json_decode(file_get_contents($cacheFile), true);
			} else {
				return "Não foi possível obter a previsão do tempo.";
			}
		}
		// Preparação do widget
		if ($dados['cod'] != 200) {
			return "Não foi possível obter a previsão do tempo para $cidade.";
		} else {
			$iconCode = $dados['weather'][0]['icon'];
			$iconUrl = 'data:image/svg+xml;base64,' . $icons[$iconCode];
			// Define a cor de fundo: azul para dia, cinza escuro para noite
			$backgroundColor = strpos($iconCode, 'n') !== false ? '#223355' : '#0066cc';

			$cidade = explode(',', $cidade, 2)[0];
			$temperatura = (int) $dados['main']['temp'];

			// HTML do widget
			$widgetHtml = "<div style='display: inline-flex; margin-left: 40%; gap: 4px; justify-content: center; align-items: center; font-family: sans-serif; font-size: 24px; font-weight: 400; background-color: $backgroundColor; background-image: linear-gradient(180deg, transparent 0%, rgba(255,255,255,0.3) 80%); color: #fff; padding: 4px 25px; border-radius: 10px; text-align: center; text-shadow: 1.5px 1.5px 1.5px rgba(0,0,0,0.4); box-shadow:  1.5px 1.5px 1.5px rgba(0,0,0,0.2); user-select: none;'>";
			$widgetHtml .= "<div>" . htmlentities($cidade) . "</div>";
			$widgetHtml .= "<img src='{$iconUrl}' alt='Ícone do tempo' style='width: 90px; height: 90px; filter: drop-shadow(0px 0px 2px rgba(0,0,0,0.3))'>";
			$widgetHtml .= "<div style='font-weight: bold;'>" . $temperatura . "°C</div>";
			$widgetHtml .= "</div>";

			return $widgetHtml;
		}
	}
	// Substitua SUA_CHAVE_API pela sua chave de API real
	$apiKey = "ab2b17afe25aceef41a7eee83d5f4d39";
	// Informe a localidade no formato "Cidade,Estado,País"
	//$localidade = " ";
	$cidade = $_POST["cidade"];
	$temperatura = $_POST["cidade"];
	// Exibe a localidade e a temperatura
	echo getWeatherWidget($cidade, $apiKey);
	//require_once "../../src/database/user.php";
	?>
	<form action="../pages/user/consulta.php" method="POST" >
	<div class="modal">
		<h5 class="modal-title"><a>TEMPERATURA</a></h5>
		<br>
	    <br>
		<label class="col-sm-2 col-form-label"><b>NOME:</b></label>
		<br>
		<label class="col-sm-2 col-form-label"><b>Cidade: </b><?= $cidade?></label>
		<br>
		<label class="col-sm-2 col-form-label"><b>Resultado: </b><?= $temperatura?></label>
		<br>
